css:
.print-format table, .print-format tr, 
.print-format td, .print-format div, .print-format p {
	line-height: 150%;
	vertical-align: middle;
}
.tbl-items-header{
    border-bottom: 2px solid black;
}
.tbl-items-subtotal {
     border-top: 2px solid black;
}

.tbl-header, .tbl-items-header, .tbl-items-details, .tbl-items-summary, .tbl-items-subtotal, .tbl-summary{
    font-size: 12px;
}
.tbl-header td, .tbl-items-subtotal td, .tbl-summary td, td.tbl-items-header td {
    padding: 0 !important;
}
.tbl-items-header td {
    font-weight:bold;
}
@media screen {
	.print-format {
		width: 4in;
		padding: 0.25in;
		min-height: 8in;
	}
}
